package allinone.business;

import org.slf4j.Logger;

import vn.game.common.LoggerContext;
import vn.game.protocol.AbstractBusiness;
import vn.game.protocol.IRequestMessage;
import vn.game.protocol.IResponsePackage;
import vn.game.protocol.MessageFactory;
import vn.game.session.ISession;
import allinone.data.AIOConstants;
import allinone.protocol.messages.GetResourceRequest;
import allinone.protocol.messages.GetResourceResponse;

public class GetResourceBusiness extends AbstractBusiness {

	private static final Logger mLog = LoggerContext.getLoggerFactory()
			.getLogger(GetResourceBusiness.class);
        
        private static int[] gameItemId = {1, 2, 3, 4, 5, 6, 7};
        private static String emoticonDetail ="";
        private static String baucuaDetail ="";
        private static String baucuaNhoDetail ="";
        private static String dia ="";
        private static String diaNho ="";        
        private static String diaTo ="";
        private static String kimcuongline ="";
        
        private static String[] gameItemDetail={emoticonDetail, baucuaDetail, baucuaNhoDetail, dia, diaNho, diaTo, kimcuongline};
        
        private static int GAME_ITEM_TYPE =1;
        
        
	public int handleMessage(ISession aSession, IRequestMessage aReqMsg,
			IResponsePackage aResPkg) {

		
		MessageFactory msgFactory = aSession.getMessageFactory();
		GetResourceResponse resBoc = (GetResourceResponse) msgFactory
				.getResponseMessage(aReqMsg.getID());
		try {
                    GetResourceRequest rqResource = (GetResourceRequest) aReqMsg;
//                    StringBuilder sb = new StringBuilder();
                    
                    if(rqResource.type == GAME_ITEM_TYPE)
                    {
                        int index = rqResource.itemId -1;
                        if(index>-1 && index < gameItemDetail.length)
                        {
                            StringBuilder sb = new StringBuilder();
                            sb.append(rqResource.itemId).append(AIOConstants.SEPERATOR_BYTE_1);
                            sb.append(gameItemDetail[index]);
                            resBoc.setSuccess(sb.toString());
                            return 1;
                        }
                    }
                    
                    resBoc.setFailure("Không tồn tại item");;
                    
                    
                        
		} finally {
			aResPkg.addMessage(resBoc);
		}
		return 1;
	}

	
}
